var first=parseInt(prompt(), 10);
var second=parseInt(prompt(), 10);
var third=parseInt(prompt(), 10);
if(first < second && first < third) {
    console.log(first);
}
else if(second < third){
    console.log(second);
}
else {
    console.log(third);
}

