exe2.examplecode:
var first=parseInt(prompt(), 10);
var second=parseInt(prompt(), 10);
var third=parseInt(prompt(), 10);
if(first < second && first < third) {
    console.log(first);
}
else if(second < third){
    console.log(second);
}
else {
    console.log(third);
}

exe3.examplecode:
let x = parseInt(prompt(), 10);
var result = "";
for(var i = 0; i < x; i++) {
  console.log(new Array(x).fill(x).toString().replace(/,/g, " "));
}

exe4.examplecode:
let x=parseInt(prompt(), 10);
let d=prompt();
let y=parseInt(prompt(), 10);
switch(d) {
  case "+":
    console.log(x+y);
  break;
  case "-":
    console.log(x-y);
  break;
  case "*":
    console.log(x*y);
  break;
  case "/":
    console.log(x/y);
  break;
}

exe5.examplecode:
let arr = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
let n = parseInt(prompt(), 10);
if(n >= 0 && n <= 7) {
  console.log(arr[n-1]);
}
else {
  console.log("Invalid day!");
}
